import React, { useState, useEffect, useRef } from "react";
import * as bootstrap from "bootstrap";
import { Toast } from "bootstrap";

function ToastDemo() {
  var [toast, setToast] = useState(false);
  const toastRef = useRef();

  useEffect(() => {
    var myToast = toastRef.current;
    var bsToast = bootstrap.Toast.getInstance(myToast);

    if (!bsToast) {
      // initialize Toast
      bsToast = new Toast(myToast, { autohide: false });
      // hide after init
      bsToast.hide();
      setToast(false);
    } else {
      // toggle
      toast ? bsToast.show() : bsToast.hide();
    }
  });

  return (
    <div className="py-2">
      <button
        className="btn btn-success"
        onClick={() => setToast((toast) => !toast)}
      >
        Toast {toast ? "hide" : "show"}
      </button>
      <div className="toast position-absolute m-4" role="alert" ref={toastRef}>
        <div className="toast-body">Hello, world! This is a toast message.</div>
      </div>
    </div>
  );
}

const Signup = () => {
  const [email, setemail] = useState();
  const [name, setname] = useState();
  const [password, setpassword] = useState();
  const [confirmpassword, setconfirmpassword] = useState();
  const [pic, setpic] = useState();
  const [Loading, setLoading] = useState(false);
  const [imagedata, setimagedata] = useState([]);

  const postdetails = (pics) => {
    setLoading(true);
    if (pic === undefined) {
      ToastDemo();
    }
    if (pics.type === "image/jpeg" || pics.type === "image/png") {
      const data = new FormData();
      data.append("file", pics);
      data.append("upload_preset", "MERN project");
      data.append("cloud_name", "dxg3ppjqx");
      fetch("https://api.cloudinary.com/v1_1/dxg3ppjqx/image/upload", {
        method: "post",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setpic(data.url.toString());
          setimagedata(data);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    } else {
      ToastDemo();
      setLoading(false);
    }
  };

  const [isshown, setisshown] = useState(false);
  const [isconfirmshown, setconfirmisshown] = useState(false);
  const togglePassword = () => {
    setisshown((isshown) => !isshown);
  };
  const toggleConfirmPassword = () => {
    setconfirmisshown((isconfirmshown) => !isconfirmshown);
  };
  const submithandler = () => {};

  return (
    <form
      className="mb-3 needs-validation StanderdizedBox"
      noValidate
      style={{ width: "90%", height: "55%" }}
    >
      <label>Email Address</label>
      <div className="form-floating">
        <input
          type="email"
          className="form-control"
          id="floatingInput"
          placeholder="name@example.com"
          onChange={(e) => setemail(e.target.value)}
          required
        ></input>
        <label htmlFor="floatingInput">example@mail.com</label>
      </div>
      <br></br>
      <label>Name</label>
      <div className="form-floating">
        <input
          type="text"
          className="form-control"
          id="floatingInput"
          placeholder="First Last"
          onChange={(e) => setname(e.target.value)}
          required
        ></input>
        <label htmlFor="floatingInput">Firstname Lastname</label>
      </div>
      <br></br>
      <label>Password</label>
      <div className="input-group">
        <input
          type={isshown ? "text" : "password"}
          className="form-control"
          id="floatingInput"
          placeholder=""
          onChange={(e) => setpassword(e.target.value)}
          required
        ></input>
        <button
          className="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
          checked={isshown}
          onClick={togglePassword}
        >
          ShowPassword
        </button>
      </div>
      <br></br>
      <label>Confirm Password</label>
      <div className="input-group">
        <input
          type={isconfirmshown ? "text" : "password"}
          className="form-control"
          id="floatingInput"
          placeholder=""
          onChange={(e) => setconfirmpassword(e.target.value)}
          required
        ></input>
        <button
          className="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
          checked={isconfirmshown}
          onClick={toggleConfirmPassword}
        >
          Show Password
        </button>
      </div>
      <br></br>
      <div className="mb-3">
        <label htmlFor="formFile" className="form-label">
          Upload Profile Picture
        </label>
        <input
          className="form-control"
          type="file"
          id="formFile"
          onChange={(e) => postdetails(e.target.files[0])}
        ></input>
      </div>
      <br></br>
      <div className="col text-center">
        <button
          type="button"
          className="btn btn-primary ButtonSize StanderdizedBox"
          onClick={postdetails}
          //isLoading={Loading}
        >
          Sign Up
        </button>
      </div>
      <div>
        <button
          type="button"
          className="btn btn-primary"
          id="toastbtn"
          onClick={postdetails}
        >
          Initialize toast
        </button>
      </div>
    </form>
  );
};

export default Signup;
