import React, { useState } from "react";

const Signup = () => {
  const [email, setemail] = useState();
  const [name, setname] = useState();
  const [password, setpassword] = useState();
  const [confirmpassword, setconfirmpassword] = useState();
  const [pic, setpic] = useState();

  const [isshown, setisshown] = useState(false);
  const [isconfirmshown, setconfirmisshown] = useState(false);
  const togglePassword = () => {
    setisshown((isshown) => !isshown);
  };
  const toggleConfirmPassword = () => {
    setconfirmisshown((isconfirmshown) => !isconfirmshown);
  };

  return (
    <form
      class="mb-3 needs-validation StanderdizedBox"
      novalidate
      style={{ width: "90%", height: "55%" }}
    >
      <label>Email Address</label>
      <div class="form-floating">
        <input
          type="email"
          class="form-control"
          id="floatingInput"
          placeholder="name@example.com"
          onChange={(e) => setemail(e.target.value)}
          required
        ></input>
        <label for="floatingInput">example@mail.com</label>
      </div>
      <br></br>
      <label>Name</label>
      <div class="form-floating">
        <input
          type="text"
          class="form-control"
          id="floatingInput"
          placeholder="First Last"
          onChange={(e) => setname(e.target.value)}
          required
        ></input>
        <label for="floatingInput">Firstname Lastname</label>
      </div>
      <br></br>
      <label>Password</label>
      <div class="input-group">
        <input
          type={isshown ? "text" : "password"}
          class="form-control"
          id="floatingInput"
          placeholder=""
          onChange={(e) => setpassword(e.target.value)}
          required
        ></input>
        <button
          class="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
          checked={isshown}
          onClick={togglePassword}
        >
          ShowPassword
        </button>
      </div>
      <br></br>
      <label>Confirm Password</label>
      <div class="input-group">
        <input
          type={isconfirmshown ? "text" : "password"}
          class="form-control"
          id="floatingInput"
          placeholder=""
          onChange={(e) => setconfirmpassword(e.target.value)}
          required
        ></input>
        <button
          class="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
          checked={isconfirmshown}
          onClick={toggleConfirmPassword}
        >
          Show Password
        </button>
      </div>
      <br></br>
      <div class="mb-3">
        <label for="formFile" class="form-label">
          Upload Profile Picture
        </label>
        <input
          class="form-control"
          type="file"
          id="formFile"
          onChange={(e) => postdetails(e.target.files[0])}
        ></input>
      </div>
    </form>
  );
};

export default Signup;
