import React, { useState } from "react";

const Signup = () => {
  const [email, setemail] = useState();
  const [name, setname] = useState();
  const [password, setpassword] = useState();
  const [confirmpassword, setconfirmpassword] = useState();
  const [pic, setpic] = useState();
  const [loading, setloading] = useState(false);
  const postdetails = (pics) => {
    setloading(true);
    if (pic === undefined) {
      <div class="toast" role="alert" aria-live="assertive" aria-atomic="true">
        <div class="toast-header">
          <img src="..." class="rounded me-2" alt="..." />
          <strong class="me-auto">Notification</strong>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="toast"
            aria-label="Close"
          ></button>
        </div>
        <div class="toast-body">Please select an image</div>
      </div>;
    }
    if (pics.type === "image/jpeg" || pics.type === "image/png") {
      const data = new FormData();
      data.append("file", pics);
      data.append("upload_preset", "MERN project");
      data.append("cloud_name", "dxg3ppjqx");
      fetch("https://api.cloudinary.com/v1_1/dxg3ppjqx/image/upload", {
        method: "post",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setpic(data.url.toString());
          // console.log(data.url.toString());
          setloading(false);
        })
        .catch((err) => {
          console.log(err);
          setloading(false);
        });
    } else {
      <div class="toast" role="alert" aria-live="assertive" aria-atomic="true">
        <div class="toast-header">
          <img src="..." class="rounded me-2" alt="..." />
          <strong class="me-auto">Error</strong>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="toast"
            aria-label="Close"
          ></button>
        </div>
        <div class="toast-body">Please select an image</div>
      </div>;
    }
  };
  const [isshown, setisshown] = useState(false);
  const [isconfirmshown, setconfirmisshown] = useState(false);
  const togglePassword = () => {
    setisshown((isshown) => !isshown);
  };
  const toggleConfirmPassword = () => {
    setconfirmisshown((isconfirmshown) => !isconfirmshown);
  };
  const submithandler = () => {};

  return (
    <form
      class="mb-3 needs-validation StanderdizedBox"
      novalidate
      style={{ width: "90%", height: "55%" }}
    >
      <label>Email Address</label>
      <div class="form-floating">
        <input
          type="email"
          class="form-control"
          id="floatingInput"
          placeholder="name@example.com"
          onChange={(e) => setemail(e.target.value)}
          required
        ></input>
        <label for="floatingInput">example@mail.com</label>
      </div>
      <br></br>
      <label>Name</label>
      <div class="form-floating">
        <input
          type="text"
          class="form-control"
          id="floatingInput"
          placeholder="First Last"
          onChange={(e) => setname(e.target.value)}
          required
        ></input>
        <label for="floatingInput">Firstname Lastname</label>
      </div>
      <br></br>
      <label>Password</label>
      <div class="input-group">
        <input
          type={isshown ? "text" : "password"}
          class="form-control"
          id="floatingInput"
          placeholder=""
          onChange={(e) => setpassword(e.target.value)}
          required
        ></input>
        <button
          class="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
          checked={isshown}
          onClick={togglePassword}
        >
          ShowPassword
        </button>
      </div>
      <br></br>
      <label>Confirm Password</label>
      <div class="input-group">
        <input
          type={isconfirmshown ? "text" : "password"}
          class="form-control"
          id="floatingInput"
          placeholder=""
          onChange={(e) => setconfirmpassword(e.target.value)}
          required
        ></input>
        <button
          class="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
          checked={isconfirmshown}
          onClick={toggleConfirmPassword}
        >
          Show Password
        </button>
      </div>
      <br></br>
      <div class="mb-3">
        <label for="formFile" class="form-label">
          Upload Profile Picture
        </label>
        <input
          class="form-control"
          type="file"
          id="formFile"
          onChange={(e) => postdetails(e.target.files[0])}
        ></input>
      </div>
      <br></br>
      <div class="col text-center">
        <button
          type="button"
          class="btn btn-primary ButtonSize StanderdizedBox"
          onClick={submithandler}
        >
          Sign Up
        </button>
      </div>
    </form>
  );
};

export default Signup;
