import React, { useState } from "react";
import ReactTooltip from "react-tooltip";
import { Menu, MenuItem, MenuButton } from "@szhsin/react-menu";
import "@szhsin/react-menu/dist/index.css";
import "@szhsin/react-menu/dist/transitions/slide.css";

const SideDrawer = () => {
  const [search, setSearch] = useState("");
  const [searchResult, setsearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState();

  return (
    <>
      <div
        className="card, Banner rounded-3"
        style={{ background: "white", margin: "10px" }}
      >
        <button
          type="button"
          className="btn btn-secondary BasicFont"
          data-tip="Search users and chats."
          data-for="registerTip"
        >
          <i className="fa-solid fa-magnifying-glass fa-xl gaping"></i>
          Search
        </button>
        <ReactTooltip id="registerTip" place="top" effect="solid">
          Search users and chats.
        </ReactTooltip>
        <div className=" text-center title biggerFont">Konverse</div>
        <Menu menuButton={<MenuButton>Open menu</MenuButton>}>
          <MenuItem>New File</MenuItem>
          <SubMenu label="Open">
            <MenuItem>index.html</MenuItem>
            <MenuItem>example.js</MenuItem>
            <SubMenu label="Styles">
              <MenuItem>about.css</MenuItem>
              <MenuItem>home.css</MenuItem>
              <MenuItem>index.css</MenuItem>
            </SubMenu>
          </SubMenu>
          <MenuItem>Save</MenuItem>
        </Menu>
      </div>
    </>
  );
};

export default SideDrawer;
