import React, { useState } from "react";
import * as bootstrap from "bootstrap";

const NotificationOption = {
  animation: true,
  container: "body",
  placement: "bottom",
  trigger: "hover focus",
};

const SideDrawer = () => {
  const [search, setSearch] = useState("");
  const [searchResult, setsearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState();

  return (
    <>
      <div
        className="card, StanderdizedBox rounded"
        style={{ background: "white", margin: "10px" }}
      >
        <button
          type="button"
          className="btn btn-secondary"
          id="tooltips"
          data-bs-toggle="tooltip"
          data-bs-custom-class="custom-tooltip"
          data-bs-title="Search users and chats."
        >
          Search
        </button>
      </div>
      <script>
        const exampleEl = document.getElementById("tooltips"); const tooltip =
        new bootstrap.Tooltip(exampleEl, NotificationOption);
      </script>
    </>
  );
};

export default SideDrawer;
